INSERT INTO `pages`
VALUES ('JhuMGNgn4', '2', '2022-07-14 07:52:54', 'max', 'L_254KKf5', '0', 'Cat', 'Cat', 'https://icatcare.org/app/uploads/2018/07/Thinking-of-getting-a-cat.png', '{\"time\":1657783117639,\"blocks\":[{\"id\":\"poldP-1IgU\",\"type\":\"paragraph\",\"data\":{\"text\":\"&nbsp; The&nbsp;<b>cat</b>&nbsp;(<i>Felis catus</i>) is a&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Domestication\\\">domestic</a><a href=\\\"https://en.wikipedia.org/wiki/Species\\\">species</a>&nbsp;of small&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Carnivorous\\\">carnivorous</a><a href=\\\"https://en.wikipedia.org/wiki/Mammal\\\">mammal</a>.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Linnaeus1758-1\\\">[1]</a><a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-MSW3fc-2\\\">[2]</a>&nbsp;It is the only domesticated species in the family&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Felidae\\\">Felidae</a>&nbsp;and is often referred to as the&nbsp;<b>domestic cat</b>&nbsp;to distinguish it from the wild members of the family.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Clutton-Brock1999-4\\\">[4]</a>&nbsp;A cat can either be a&nbsp;<b>house cat</b>, a&nbsp;<b><a href=\\\"https://en.wikipedia.org/wiki/Farm_cat\\\">farm cat</a></b>&nbsp;or a&nbsp;<b><a href=\\\"https://en.wikipedia.org/wiki/Feral_cat\\\">feral cat</a></b>; the latter ranges freely and avoids human contact.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Liberg_al2014-5\\\">[5]</a>&nbsp;Domestic cats are valued by humans for companionship and their ability to kill&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Rodent\\\">rodents</a>. About 60&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Cat_breeds\\\">cat breeds</a>&nbsp;are recognized by various&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Cat_registries\\\">cat registries</a>.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Driscoll_al2009-6\\\">[6]</a>\"}}],\"version\":\"2.25.0\"}', '{\"time\":1657783117640,\"blocks\":[{\"id\":\"pO8FTvLflt\",\"type\":\"header\",\"data\":{\"text\":\"Taxonomy\",\"level\":2}},{\"id\":\"Xf38MppkNe\",\"type\":\"paragraph\",\"data\":{\"text\":\"The&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Scientific_name\\\">scientific name</a>&nbsp;<i>Felis catus</i>&nbsp;was proposed by&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Carl_Linnaeus\\\">Carl Linnaeus</a>&nbsp;in 1758 for a domestic cat.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Linnaeus1758-1\\\">[1]</a><a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-MSW3fc-2\\\">[2]</a>&nbsp;<i>Felis catus domesticus</i>&nbsp;was proposed by&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Johann_Christian_Polycarp_Erxleben\\\">Johann Christian Polycarp Erxleben</a>&nbsp;in 1777.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Erxleben-3\\\">[3]</a>&nbsp;<i>Felis daemon</i>&nbsp;proposed by&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Konstantin_Alekseevich_Satunin\\\">Konstantin Alekseevich Satunin</a>&nbsp;in 1904 was a black cat from the&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Transcaucasus\\\">Transcaucasus</a>, later identified as a domestic cat.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-35\\\">[35]</a><a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-36\\\">[36]</a>\"}},{\"id\":\"WXIcRwhv90\",\"type\":\"paragraph\",\"data\":{\"text\":\"In 2003, the&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/International_Commission_on_Zoological_Nomenclature\\\">International Commission on Zoological Nomenclature</a>&nbsp;ruled that the domestic cat is a distinct species, namely&nbsp;<i>Felis catus</i>.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-ICZN-37\\\">[37]</a><a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-38\\\">[38]</a>&nbsp;In 2007, it was considered a&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Subspecies\\\">subspecies</a>,&nbsp;<i>F. silvestris catus</i>, of the&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/European_wildcat\\\">European wildcat</a>&nbsp;(<i>F. silvestris</i>) following results of&nbsp;<a href=\\\"https://en.wikipedia.org/wiki/Phylogenetic\\\">phylogenetic</a>&nbsp;research.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-Driscoll-39\\\">[39]</a><a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-MSW3fs-40\\\">[40]</a>&nbsp;In 2017, the IUCN Cat Classification Taskforce followed the recommendation of the ICZN in regarding the domestic cat as a distinct species,&nbsp;<i>Felis catus</i>.<a href=\\\"https://en.wikipedia.org/wiki/Cat#cite_note-41\\\">[41]</a>\"}}],\"version\":\"2.25.0\"}');



